import { Rate} from 'antd';
import React , { useEffect, useState} from  'react' ; 
import { FrownOutlined, MehOutlined, SmileOutlined } from '@ant-design/icons';

import axios from 'axios'

const API_ROOT = 'http://localhost:4000/api'
const instance = axios.create({
  baseURL: API_ROOT
})


const customIcons = {
    1: <FrownOutlined />,
    2: <FrownOutlined />,
    3: <MehOutlined />,
    4: <SmileOutlined />,
    5: <SmileOutlined />,
  };


export default function DiaryContent({dateStr, accountId}){
    const [mood, setMood] = useState(3);
    const [text, setText] = useState("");
    const [isInDatabase, setIsInDatabase] = useState(false);

    const mood_onChange = (v) =>{
        setMood(v);
    }

    const text_onChange = (e) => {
        //console.log(e.target.value);
        setText(e.target.value);
    }

    const _getContent = async () => {
        const {
            data: { message, content}
        } = await instance.get('/getContent',{ params: { accountId, dateStr}});
        if(message==="exist"){
            //console.log(content);
            setMood(content.mood);
            setText(content.text);
            setIsInDatabase(true);
        }
    }

    const _addContent = async () =>{
        const {
            data: { message}
        } = await instance.post('/addContent',{ params: { accountId, dateStr, mood, text}});
        if(message==="success")setIsInDatabase(true);
    }

    const _updateContent = async () => {
        const {
            data: { message}
        } = await instance.post('/updateContent',{ params: { accountId, dateStr, mood, text}});
        if(message === "error") alert("update diary error")
    }

    const onSave = () =>{
        if(isInDatabase){
            _updateContent();
        }else{
            _addContent();
        }
    }

    useEffect(()=>{
        if(dateStr!==undefined){
            _getContent();
        }
    },[dateStr]);

    return(
        <div>
        <div className="today font_J">
            <div className="today_title">Today : </div>
            <div className="today_date">{dateStr}</div>
        </div>
        <Rate value={mood} onChange={mood_onChange}character={({ index }) => customIcons[index + 1]}></Rate>

        <textarea placeholder="Record your beautiful Love Memory..."
            rows="12" cols="40"
            onChange = {text_onChange}
            value={text}
        ></textarea>
        <button onClick={onSave}>save</button>

        </div>
    )
}
