import {React, useState, useEffect} from 'react';

export default function PicturesWall({dateStr, accountId, photoText, photoText_onChange}){
  const [imageUrl, setImageUrl] = useState("");

  const uploadImage = ({}) => {
    const r = new XMLHttpRequest()
    const d = new FormData()
    const e = document.getElementsByClassName('input-image')[0].files[0]
    var u

    d.append('image', e)

    r.open('POST', 'https://api.imgur.com/3/image/')
    r.setRequestHeader('Authorization', `Client-ID 4e3b0d0edce7854`)
    r.onreadystatechange = function () {
      if(r.status === 200 && r.readyState === 4) {
        let res = JSON.parse(r.responseText)
        u = `https://i.imgur.com/${res.data.id}.png`
      }      
      setImageUrl(u);
      console.log(u);
    }
    r.send(d);
  }

  return (
    <>
      {imageUrl? <img className="photo" alt="photo" src={imageUrl}/>:<></>}
      <label for="fileinp">
        <input type="button" id="btn" value="點我上傳"/>
        <input type="file" id="fileinp" className="input-image" onChange={uploadImage}/>
      </label>
      <textarea value={photoText} onChange={photoText_onChange} placeholder="Someting about the photo..."></textarea>
    </>
  );
}
