import { Rate } from 'antd';
import React , {useEffect, useState} from  'react' ; 
import { FrownOutlined, MehOutlined, SmileOutlined } from '@ant-design/icons';
const customIcons = {
    1: <FrownOutlined />,
    2: <FrownOutlined />,
    3: <MehOutlined />,
    4: <SmileOutlined />,
    5: <SmileOutlined />,
  };

export default function DiaryContent(){
    const [todayStr, setTodayStr] = useState("");
    const [mood, setMood] = useState(3);

    const mood_onChange = (v) =>{
        setMood(v);
    }

    useEffect(()=>{
        const day =  new  Date ( ). getDate ( ) ; 
        const month =  new  Date ( ) . getMonth ( )  +  1 ; 
        const year =  new  Date ( ) . getFullYear ( ) ;
        setTodayStr(String(year)+"/"+String(month)+"/"+String(day));
    },[]);

    return(
        <div>
        <div className="today font_J">
            <div className="today_title">Today : </div>
            <div className="today_date">{todayStr}</div>
        </div>
        <Rate value={mood} onChange={mood_onChange}character={({ index }) => customIcons[index + 1]}></Rate>
        </div>
    )
}
