import React , {useState, useEffect}from 'react'
import { NavLink } from "react-router-dom";
import PicturesWall from "./PicturesWall";
import DiaryContent from "./DiaryContent";
// import Button from "antd-button-color";
import { DoubleLeftOutlined ,  DoubleRightOutlined } from '@ant-design/icons';
import moment from 'moment';

import "./Diary.css"

export default function Diary({accountId}){
    const [dateStr, setDateStr] = useState();
    const goLeftPage = () =>{
        const newDate = moment(dateStr).add(1,"days").format("YYYY-MM-DD");
        setDateStr(newDate);
    }

    const goRightPage = () =>{
        const newDate = moment(dateStr).subtract(1,"days").format("YYYY-MM-DD");
        setDateStr(newDate);
    }

    useEffect(()=>{
        let str = new Date().toISOString().replace(/T.*$/, ''); // YYYY-MM-DD of today
        setDateStr(str);
    },[]);
    return (
        <div className='boook'>
            <div className="list">
                <button className='buttons'type="button" >
                    <NavLink to="/">Home</NavLink>
                </button>
                <p></p>
                <button className='buttons' type="button">
                    <NavLink to="/Calendar">Calendar</NavLink>
                </button>
                <p></p>
                <button className='buttons now' disabled={true} type="button">
                    <NavLink to="/Diary">Diary</NavLink>
                </button>
            </div>
            <div className='boookleft'>
                <DoubleLeftOutlined onClick={goLeftPage}/>
                <DiaryContent dateStr={dateStr} accountId={accountId}></DiaryContent>
            </div>
            <div className='boookright'>
                <PicturesWall dateStr={dateStr} accountId={accountId}></PicturesWall>
                <DoubleRightOutlined onClick={goRightPage}/>
            </div>
        </div>
    );
}
